import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Question } from 'src/app/models/Question';
import { ForumService } from 'src/app/service/forum.service';
@Component({
  selector: 'app-ajoutquestion',
  templateUrl: './ajoutquestion.component.html',
  styleUrls: ['./ajoutquestion.component.css']
})
export class AjoutquestionComponent {

  constructor(private forumservice : ForumService)
  {

  }

  ajouterquestion(ahmed:NgForm)
  {
    let nom : string =ahmed.value.nom;
    let prenom:string =ahmed.value.prenom;
    let question = new Question(nom,prenom);

   this.forumservice.ajouterquestion(question).subscribe(
    {
      next: (data)=>
      {
          alert("ajouter avec succéé")
      },
      error: (err)=>
      {
        alert("il y a un erreur ")
      }
    }
   )
  }



}
